#
from	__future__ import annotations
from	enum import Enum
#
class MediaType (Enum):
	EMAIL = 0
class UserStatus (Enum):
	ACTIVE = 1
	BOUNCE = 2
	ADMOUT = 3
	OPTOUT = 4
	WAITCONFIRM = 5
	BLACKLIST = 6
	@classmethod
	def find_status (cls, name: str) -> UserStatus:
		return cls.__members__[name.upper ()]
